mapper.sql:
rpt.item_id item_id, rpt.description 'desc', sum(subqnt) qnt, count(distinct sub_ord_cnt) num_orders, (sum(sub_amt)
* (1.0 - #{vatRate,jdbcType=DOUBLE})
) as amount

mapper.select:
SELECT
FROM (SELECT i_0.*, i_0.quantity as subqnt, op.order_id as sub_ord_cnt, i_0.amount + if(i_1.price is null, 0, (i_0.amount * if(i_1.price is null, - 1, sum(i_1.price) / 100))) as sub_amt FROM invoice_line i_0 Left outer join order_process op ON i_0.invoice_id=op.invoice_id Left outer join invoice_line i_1 ON i_0.invoice_id=i_1.invoice_id AND i_0.item_id not in (SELECT it.id from item it WHERE it.percentage is not null) AND i_1.item_id not in (SELECT it.id from item it WHERE it.percentage is null ) WHERE
i_0.item_id in
#{item}
AND
i_0.invoice_id in (SELECT inv.id FROM invoice inv WHERE
and inv.create_datetime >= #{startDate,jdbcType=TIMESTAMP}
and inv.create_datetime <= #{endDate,jdbcType=TIMESTAMP}
AND inv.currency_id = #{currencyId,jdbcType=INTEGER}
AND inv.user_id = #{userId,jdbcType=INTEGER}
AND inv.user_id in (SELECT con.user_id FROM contact con WHERE con.city = #{city,jdbcType=INTEGER})
AND
inv.deleted = 1
inv.deleted = 0
AND inv.is_review = 0 ) group by i_0.invoice_id, i_0.item_id) rpt group by rpt.item_id;
