mapper.select:
select c.user_id user_id, cf.content fullname from contact c, contact_field cf Where cf.contact_id = c.id
AND c.city = #{city, jdbcType=INTEGER}
AND cf.type_id = 432 AND cf.content like #{name, jdbcType=VARCHAR}

select cc.id user_id, (invoice_balance - payment_balance) balance from ( select i.user_id id, sum(balance) invoice_balance, ifnull(v.payment_balance, 0) payment_balance from invoice i left join (select user_id, sum(balance) as payment_balance from payment where deleted = 0 and user_id in (select b.id from base_user b where b.entity_id = 1
AND b.currency_id = #{currencyId,jdbcType=INTEGER}
and
b.deleted = 1
b.deleted = 0
) group by user_id) v on i.user_id = v.user_id where i.deleted = 0 and i.user_id in (select b.id from base_user b where b.entity_id = 1
AND b.currency_id = #{currencyId,jdbcType=INTEGER}
and
b.deleted = 1
b.deleted = 0
) group by i.user_id union select i.user_id id, sum(balance) invoice_balance, ifnull(v.payment_balance, 0) payment_balance from invoice i right join (select user_id, sum(balance) as payment_balance from payment where deleted = 0 and user_id in (select b.id from base_user b where b.entity_id = 1
AND b.currency_id = #{currencyId,jdbcType=INTEGER}
and
b.deleted = 1
b.deleted = 0
) group by user_id) v on i.user_id = v.user_id where i.deleted = 0 and i.user_id in (select b.id from base_user b where b.entity_id = 1
AND b.currency_id = #{currencyId,jdbcType=INTEGER}
and
b.deleted = 1
b.deleted = 0
) group by v.user_id ) cc
join contact c on c.user_id = cc.id AND c.city = #{city,jdbcType=INTEGER}
where (invoice_balance - payment_balance) > 0;

select c.user_id 'user_id', cf.content 'fullname', cf1.content 'username', STR_TO_DATE(cf2.content, '%m/%d/%y') 'install_time', c.city 'city', (sum(i.total)
* (1.0 - #{vatRate,jdbcType=DOUBLE})
) 'balance' from contact c join invoice i ON i.user_id = c.user_id AND i.deleted = 0 AND i.is_review = 0 AND i.status_id = 27
AND i.currency_id = #{currencyId,jdbcType=INTEGER}
and i.create_datetime >= #{startDate,jdbcType=TIMESTAMP}
and i.create_datetime <= #{endDate,jdbcType=TIMESTAMP}
join contact_field cf ON cf.contact_id = c.id AND cf.type_id = 432 join contact_field cf1 ON cf1.contact_id = c.id AND cf1.type_id = 434 join contact_field cf2 ON cf2.contact_id = c.id AND cf2.type_id = 436
join contact_field cf3 on cf3.contact_id = c.id AND cf3.type_id = 435
join contact_field cf4 on cf4.contact_id = c.id AND cf4.content = 443
AND c.city = #{city, jdbcType=INTEGER}
AND cf3.content = #{partnerId, jdbcType=INTEGER}
AND cf4.content = #{salesmanId, jdbcType=INTEGER}
group by c.user_id order by sum(i.total)
desc
